<?php
function generateRandomString($length = 9) { $characters = '0123456789'; $charactersLength = strlen($characters); $randomString = ''; for ($i = 0; $i < $length; $i++) { $randomString .= $characters[rand(0, $charactersLength - 1)]; } return "811".$randomString; } function check($code){ $getContents = file_get_contents("http://jis.ooo/kfc/12digit.php?v=".$code); $jDecode = json_decode($getContents); if ($jDecode->status == "valid"){ echo $code." => VALID"; }else{ echo $code." => INVALID"; } } while (true){ check(generateRandomString())."\n"; }
